import React from 'react';
import { Flex, Box, List, ListItem, Icon, Text } from '@chakra-ui/react';
import { HiHome, HiFire } from 'react-icons/hi';
import { Link, useRouteMatch } from 'react-router-dom';

function NavLink({ name, icon: IconName, url }) {
  const match = useRouteMatch({ path: url, strict: true, sensitive: true });

  return (
    <Link to={url}>
      <Text
        color={match ? 'white' : '#c6d0e2'}
        display="flex"
        alignItems="center">
        <Icon
          mr="3"
          w="20px"
          h="20px"
          color={match ? 'white' : '#516381'}
          as={IconName}
        />
        {name}
      </Text>
    </Link>
  );
}

